  `title_dr` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `title_pa` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `date_en` varchar(20) COLLATE utf8mb4_unicode_ci NOT NULL,
  `date_dr` varchar(20) COLLATE utf8mb4_unicode_ci NOT NULL,
  `date_pa` varchar(20) COLLATE utf8mb4_unicode_ci NOT NULL,
  `pdf_en` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `pdf_dr` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
