CREATE TABLE `infographics` (
  `id` int(10) UNSIGNED NOT NULL,
  `title_en` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `title_dr` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `title_pa` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
