CREATE TABLE `president` (
  `id` int(10) UNSIGNED NOT NULL,
  `title_en` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `title_dr` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `title_pa` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `date_en` varchar(20) COLLATE utf8mb4_unicode_ci NOT NULL,
  `date_dr` varchar(20) COLLATE utf8mb4_unicode_ci NOT NULL,
  `date_pa` varchar(20) COLLATE utf8mb4_unicode_ci NOT NULL,
  `short_desc_en` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `short_desc_dr` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `short_desc_pa` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `description_en` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `description_dr` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `description_pa` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `image` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `type` enum('decree','order','statement','message','word') COLLATE utf8mb4_unicode_ci NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

